
public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even numbers");
		
	     for(int i=0; i<=20;i++)	
		{
			if(i%2==0) {
			System.out.println(i);
			}
		}
		
		System.out.println("Odd numbers");
		
		for(int a=0; a<=20;a++)	
		{
			if(a%2!=0) {
			System.out.println(a);
			}
		}
	}

}
